import React, { useState, useEffect } from 'react';
import API from './api';
import SymptomTracker from './SymptomTracker';
import AIAssistant from './AIAssistant';
import WellnessTracker from './WellnessTracker';
import MedicationTracker from './MedicationTracker';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState(null);
  const [isRegistering, setIsRegistering] = useState(false);
  const [activeTab, setActiveTab] = useState('symptoms');
  const [menuOpen, setMenuOpen] = useState(false);
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const [notificationCount, setNotificationCount] = useState(0);
  const [showWelcomeModal, setShowWelcomeModal] = useState(false);
  const [darkMode, setDarkMode] = useState(localStorage.getItem('darkMode') === 'true');
  const [language, setLanguage] = useState(localStorage.getItem('language') || 'it');
  const [showSettings, setShowSettings] = useState(false);

  // Dizionario di traduzioni
  const translations = {
    it: {
      login: 'Accedi',
      register: 'Registrati',
      email: 'Email',
      password: 'Password',
      name: 'Nome',
      symptoms: 'Sintomi',
      wellness: 'Benessere',
      medications: 'Farmaci',
      assistant: 'Salus',
      settings: 'Impostazioni',
      darkMode: 'Modalità scura',
      logout: 'Esci',
      language: 'Lingua',
      welcomeTitle: 'Benvenuto su Salus!',
      welcomeMessage: 'Grazie per esserti registrato. Salus ti aiuterà a monitorare la tua salute.',
      confirmEmail: 'Ti abbiamo inviato un\'email di conferma. Per favore, controlla la tua casella di posta.'
    },
    en: {
      login: 'Login',
      register: 'Register',
      email: 'Email',
      password: 'Password',
      name: 'Name',
      symptoms: 'Symptoms',
      wellness: 'Wellness',
      medications: 'Medications',
      assistant: 'Salus',
      settings: 'Settings',
      darkMode: 'Dark mode',
      logout: 'Logout',
      language: 'Language',
      welcomeTitle: 'Welcome to Salus!',
      welcomeMessage: 'Thank you for registering. Salus will help you monitor your health.',
      confirmEmail: 'We\'ve sent you a confirmation email. Please check your inbox.'
    },
    es: {
      login: 'Iniciar sesión',
      register: 'Registrarse',
      email: 'Correo electrónico',
      password: 'Contraseña',
      name: 'Nombre',
      symptoms: 'Síntomas',
      wellness: 'Bienestar',
      medications: 'Medicamentos',
      assistant: 'Salus',
      settings: 'Configuración',
      darkMode: 'Modo oscuro',
      logout: 'Cerrar sesión',
      language: 'Idioma',
      welcomeTitle: '¡Bienvenido a Salus!',
      welcomeMessage: 'Gracias por registrarte. Salus te ayudará a monitorear tu salud.',
      confirmEmail: 'Te hemos enviado un correo de confirmación. Por favor, revisa tu bandeja de entrada.'
    }
  };

  // Funzione per ottenere le traduzioni nella lingua selezionata
  const t = (key) => {
    return translations[language]?.[key] || translations['it'][key] || key;
  };

  // Salva la lingua nelle preferenze
  useEffect(() => {
    localStorage.setItem('language', language);
  }, [language]);

  // Controlla lo stato della connessione
  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Applica la modalità dark se attiva
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  // Simulazione di notifiche (in un'app reale, questo verrebbe dal backend)
  useEffect(() => {
    if (userId) {
      const interval = setInterval(() => {
        // Simula la ricezione casuale di notifiche
        if (Math.random() > 0.9) {
          setNotificationCount(prev => prev + 1);
        }
      }, 60000); // Controlla ogni minuto

      return () => clearInterval(interval);
    }
  }, [userId]);

  // Mostra il modal di benvenuto una volta al primo accesso
  useEffect(() => {
    if (userId && !localStorage.getItem('welcomeShown')) {
      setShowWelcomeModal(true);
      localStorage.setItem('welcomeShown', 'true');
    }
  }, [userId]);

  // Funzioni per le impostazioni
  const toggleSettings = () => {
    setShowSettings(!showSettings);
  };

  const changeLanguage = (newLanguage) => {
    setLanguage(newLanguage);
    setShowSettings(false);
  };

  const handleRegister = async () => {
    try {
      // Validazione dei campi
      if (!username || username.length < 3) {
        alert(t('nameValidation'));
        return;
      }

      if (!email || !email.includes('@')) {
        alert(t('emailValidation'));
        return;
      }

      if (!password || password.length < 6) {
        alert(t('passwordValidation'));
        return;
      }

      await API.post('/auth/register', { name: username, email, password });
      alert(t('confirmEmail'));
      setIsRegistering(false);
    } catch (error) {
      console.error(error);
      alert('Errore durante la registrazione: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleLogin = async () => {
    try {
      if (!email || !password) {
        alert('Compila tutti i campi richiesti');
        return;
      }

      console.log('Tentativo di login con:', { email });

      const response = await API.post('/auth/login', { 
        email, 
        password 
      });

      console.log('Risposta login:', response.data);
      const { userId, name } = response.data;

      // Salva le credenziali dell'utente
      setUserId(userId);
      localStorage.setItem('userId', userId);
      localStorage.setItem('userName', name);

      // Reset dei campi
      setEmail('');
      setPassword('');

      showToast('Login effettuato con successo', 'success');
    } catch (error) {
      console.error('Errore durante il login:', error);

      let errorMessage = 'Errore durante il login';

      if (error.response) {
        if (error.response.status === 400) {
          errorMessage = 'Email o password non validi';
        } else if (error.response.status === 401) {
          errorMessage = 'Non autorizzato. Verifica le tue credenziali.';
        } else if (error.response.status === 404) {
          errorMessage = 'Utente non trovato';
        } else if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        }
      } else if (error.request) {
        errorMessage = 'Impossibile raggiungere il server. Controlla la tua connessione.';
      }

      alert(errorMessage);
    }
  };

  const handleLogout = () => {
    setUserId(null);
    setEmail('');
    setPassword('');
    setMenuOpen(false);
    showToast('Logout effettuato');
  };

  // Funzione per mostrare toast notifications
  const showToast = (message, type = 'success') => {
    const toast = document.createElement('div');
    toast.className = `toast toast-${type} slide-in-up`;
    toast.textContent = message;
    document.body.appendChild(toast);

    setTimeout(() => {
      toast.classList.add('fade-out');
      setTimeout(() => {
        document.body.removeChild(toast);
      }, 300);
    }, 3000);
  };

  // Gestisce la chiusura del menu quando si seleziona una tab
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setMenuOpen(false);
  };

  return (
    <div className={`app ${darkMode ? 'dark-mode' : ''}`}>
      {/* Header con menu mobile */}
      {userId && (
        <header className="app-header">
          <div className="container d-flex justify-between align-center">
            <h1 className="logo" onClick={() => handleTabChange('symptoms')} style={{ cursor: 'pointer' }}>
              <i className="fas fa-heartbeat" style={{ color: 'var(--primary-600)', marginRight: '10px' }}></i>
              <span className="hide-sm">Salus</span>
            </h1>

            <div className="header-actions d-flex align-center">
              {/* Indicatore stato connessione */}
              <div className="connection-status mx-3" title={isOnline ? 'Online' : 'Offline'}>
                <i className={`fas fa-wifi ${isOnline ? 'text-success' : 'text-danger'}`}></i>
              </div>

              {/* Switch Dark Mode */}
              <button 
                className="btn btn-icon mx-2" 
                onClick={() => setDarkMode(!darkMode)}
                title={darkMode ? 'Passa alla modalità chiara' : 'Passa alla modalità scura'}
              >
