            </div>
            <div className="modal-footer">
              <button className="btn btn-outline mr-2" onClick={closeSymptomDetails}>
                <i className="fas fa-times"></i> Chiudi
              </button>
              <button className="btn btn-primary">
                <i className="fas fa-edit"></i> Modifica
              </button>
            </div>
          </div>
