import React, { useState, useEffect } from 'react';
import API from './api';
import SymptomTracker from './SymptomTracker';
import AIAssistant from './AIAssistant';
import WellnessTracker from './WellnessTracker';
import MedicationTracker from './MedicationTracker';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState(null);
  const [isRegistering, setIsRegistering] = useState(false);
  const [activeTab, setActiveTab] = useState('symptoms');
  const [menuOpen, setMenuOpen] = useState(false);
  const [isOnline, setIsOnline] = useState(navigator.onLine);
  const [notificationCount, setNotificationCount] = useState(0);
  const [showWelcomeModal, setShowWelcomeModal] = useState(false);
  const [darkMode, setDarkMode] = useState(localStorage.getItem('darkMode') === 'true');

  // Controlla lo stato della connessione
  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Applica la modalità dark se attiva
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
    localStorage.setItem('darkMode', darkMode);
  }, [darkMode]);

  // Simulazione di notifiche (in un'app reale, questo verrebbe dal backend)
  useEffect(() => {
    if (userId) {
      const interval = setInterval(() => {
        // Simula la ricezione casuale di notifiche
        if (Math.random() > 0.9) {
          setNotificationCount(prev => prev + 1);
        }
      }, 60000); // Controlla ogni minuto

      return () => clearInterval(interval);
    }
  }, [userId]);

  // Mostra il modal di benvenuto una volta al primo accesso
  useEffect(() => {
    if (userId && !localStorage.getItem('welcomeShown')) {
      setShowWelcomeModal(true);
      localStorage.setItem('welcomeShown', 'true');
    }
  }, [userId]);

  const handleRegister = async () => {
    try {
      // Validazione dei campi
      if (!username || username.length < 3) {
        alert('Il nome utente deve essere di almeno 3 caratteri');
        return;
      }

      if (!email || !email.includes('@')) {
        alert('Inserisci un indirizzo email valido');
        return;
      }

      if (!password || password.length < 6) {
        alert('La password deve essere di almeno 6 caratteri');
        return;
      }

      await API.post('/auth/register', { name: username, email, password });
      alert('Registrazione completata! Ora puoi effettuare il login.');
      setIsRegistering(false);
    } catch (error) {
      console.error(error);
      alert('Errore durante la registrazione: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleLogin = async () => {
    try {
      if (!email || !password) {
        alert('Compila tutti i campi richiesti');
        return;
      }

      const response = await API.post('/auth/login', { email, password });
      const { userId, name } = response.data;

      // Salva le credenziali dell'utente
      setUserId(userId);
      localStorage.setItem('userId', userId);
      localStorage.setItem('userName', name);

      // Reset dei campi
      setEmail('');
      setPassword('');
    } catch (error) {
      console.error(error);
      alert('Errore durante il login: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleLogout = () => {
    setUserId(null);
    setEmail('');
    setPassword('');
    setMenuOpen(false);
    showToast('Logout effettuato');
  };

  // Funzione per mostrare toast notifications
  const showToast = (message, type = 'success') => {
    const toast = document.createElement('div');
    toast.className = `toast toast-${type} slide-in-up`;
    toast.textContent = message;
    document.body.appendChild(toast);

    setTimeout(() => {
      toast.classList.add('fade-out');
      setTimeout(() => {
        document.body.removeChild(toast);
      }, 300);
    }, 3000);
  };

  // Gestisce la chiusura del menu quando si seleziona una tab
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setMenuOpen(false);
  };

  return (
    <div className={`app ${darkMode ? 'dark-mode' : ''}`}>
      {/* Header con menu mobile */}
      {userId && (
        <header className="app-header">
          <div className="container d-flex justify-between align-center">
            <h1 className="logo">
              <i className="fas fa-heartbeat" style={{ color: 'var(--primary-600)', marginRight: '10px' }}></i>
              <span className="hide-sm">Salus</span>
            </h1>

            <div className="header-actions d-flex align-center">
              {/* Indicatore stato connessione */}
              <div className="connection-status mx-3" title={isOnline ? 'Online' : 'Offline'}>
                <i className={`fas fa-wifi ${isOnline ? 'text-success' : 'text-danger'}`}></i>
              </div>

              {/* Switch Dark Mode */}
              <button 
                className="btn btn-icon mx-2" 
                onClick={() => setDarkMode(!darkMode)}
                title={darkMode ? 'Passa alla modalità chiara' : 'Passa alla modalità scura'}
              >
                <i className={`fas ${darkMode ? 'fa-sun' : 'fa-moon'}`}></i>
              </button>

              {/* Notifiche */}
              <div className={`notifications mx-2 ${notificationCount > 0 ? 'pulse-dot pulse-animation' : ''}`}>
                <i className="fas fa-bell"></i>
                {notificationCount > 0 && (
                  <span className="badge badge-danger">{notificationCount}</span>
                )}
              </div>

              {/* Menu hamburger (solo mobile) */}
              <button className="btn btn-icon hide-lg" onClick={() => setMenuOpen(!menuOpen)}>
                <i className={`fas ${menuOpen ? 'fa-times' : 'fa-bars'}`}></i>
              </button>

              {/* Pulsante logout (visibile solo su desktop) */}
              <button onClick={handleLogout} className="btn btn-outline hide-sm mx-2">
                <i className="fas fa-sign-out-alt"></i>
                <span className="ml-2">Logout</span>
              </button>
            </div>
          </div>

          {/* Menu mobile */}
          {menuOpen && (
            <div className="mobile-menu slide-in-up">
              <nav>
                <ul>
                  <li onClick={() => handleTabChange('symptoms')}>
                    <i className="fas fa-notes-medical"></i> Sintomi
                  </li>
                  <li onClick={() => handleTabChange('wellness')}>
                    <i className="fas fa-spa"></i> Benessere
                  </li>
                  <li onClick={() => handleTabChange('medications')}>
                    <i className="fas fa-pills"></i> Medicinali
                  </li>
                  <li onClick={() => handleTabChange('assistant')}>
                    <i className="fas fa-robot"></i> Assistente IA
                  </li>
                  <li onClick={handleLogout}>
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </li>
                </ul>
              </nav>
            </div>
          )}
        </header>
      )}

      <main className="app-content">
        <div className="container">
          {!userId ? (
            <div className="auth-container card fade-in">
              <div className="card-header">
                <h2 className="text-center">
                  <i className="fas fa-heartbeat" style={{ color: 'var(--primary-600)', marginRight: '10px' }}></i>
                  Salus
                </h2>
                <p className="text-center text-muted">Il tuo assistente personale per la salute</p>
              </div>

              <div className="card-body">
                <div className="tabs">
                  <div 
                    className={`tab ${!isRegistering ? 'active' : ''}`} 
                    onClick={() => setIsRegistering(false)}
                  >
                    Accedi
                  </div>
                  <div 
                    className={`tab ${isRegistering ? 'active' : ''}`}
                    onClick={() => setIsRegistering(true)}
                  >
                    Registrati
                  </div>
                </div>

                {isRegistering ? (
                  <form className="auth-form">
                    <div className="form-group">
                      <label className="form-label" htmlFor="username">
                        <i className="fas fa-user"></i> Username
                      </label>
                      <input
                        type="text"
                        id="username"
                        className="form-control"
                        placeholder="Scegli un nome utente"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label" htmlFor="register-email">
                        <i className="fas fa-envelope"></i> Email
                      </label>
                      <input
                        type="email"
                        id="register-email"
                        className="form-control"
                        placeholder="La tua email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label" htmlFor="register-password">
                        <i className="fas fa-lock"></i> Password
                      </label>
                      <input
                        type="password"
                        id="register-password"
                        className="form-control"
                        placeholder="Crea una password sicura"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                      />
                    </div>

                    <button 
                      type="button"
                      className="btn btn-primary btn-lg w-100 mt-4"
                      onClick={handleRegister}
                      disabled={!username || !email || !password}
                    >
                      <i className="fas fa-user-plus"></i> Registrati
                    </button>
                  </form>
                ) : (
                  <form className="auth-form">
                    <div className="form-group">
                      <label className="form-label" htmlFor="login-email">
                        <i className="fas fa-envelope"></i> Email
                      </label>
                      <input
                        type="email"
                        id="login-email"
                        className="form-control"
                        placeholder="La tua email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label" htmlFor="login-password">
                        <i className="fas fa-lock"></i> Password
                      </label>
                      <input
                        type="password"
                        id="login-password"
                        className="form-control"
                        placeholder="La tua password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
                      />
                    </div>

                    <div className="form-group">
                      <div className="d-flex justify-between align-center">
                        <div className="form-check">
                          <input type="checkbox" id="remember" className="form-check-input" />
                          <label htmlFor="remember" className="form-check-label">Ricordami</label>
                        </div>
                        <a href="#" className="text-primary">Password dimenticata?</a>
                      </div>
                    </div>

                    <button 
                      type="button"
                      className="btn btn-primary btn-lg w-100 mt-4"
                      onClick={handleLogin}
                      disabled={!email || !password}
                    >
                      <i className="fas fa-sign-in-alt"></i> Accedi
                    </button>
                  </form>
                )}
              </div>

              <div className="card-footer text-center">
                <p>
                  {isRegistering 
                    ? 'Hai già un account?' 
                    : 'Non hai ancora un account?'
                  }
                  <button 
                    className="btn btn-link" 
                    onClick={() => setIsRegistering(!isRegistering)}
                  >
                    {isRegistering ? 'Accedi' : 'Registrati'}
                  </button>
                </p>
              </div>
            </div>
          ) : (
            <>
              {/* Dashboard */}
              <div className="dashboard fade-in">
                {/* Tab navigation per desktop */}
                <div className="tabs hide-sm">
                  <div 
                    className={`tab ${activeTab === 'symptoms' ? 'active' : ''}`}
                    onClick={() => setActiveTab('symptoms')}
                  >
                    <i className="fas fa-notes-medical"></i> Sintomi
                  </div>
                  <div 
                    className={`tab ${activeTab === 'wellness' ? 'active' : ''}`}
                    onClick={() => setActiveTab('wellness')}
                  >
                    <i className="fas fa-spa"></i> Benessere
                  </div>
                  <div 
                    className={`tab ${activeTab === 'medications' ? 'active' : ''}`}
                    onClick={() => setActiveTab('medications')}
                  >
                    <i className="fas fa-pills"></i> Medicinali
                  </div>
                  <div 
                    className={`tab ${activeTab === 'assistant' ? 'active' : ''}`}
                    onClick={() => setActiveTab('assistant')}
                  >
                    <i className="fas fa-robot"></i> Assistente IA
                  </div>
                </div>

                {/* Contenuto della tab */}
                <div className="tab-content slide-in-up">
                  {activeTab === 'symptoms' && <SymptomTracker userId={userId} />}
                  {activeTab === 'wellness' && <WellnessTracker userId={userId} />}
                  {activeTab === 'medications' && <MedicationTracker userId={userId} />}
                  {activeTab === 'assistant' && <AIAssistant userId={userId} />}
                </div>
              </div>

              {/* Navigazione inferiore mobile */}
              <nav className="bottom-nav hide-lg">
                <div 
                  className={`bottom-nav-item ${activeTab === 'symptoms' ? 'active' : ''}`}
                  onClick={() => setActiveTab('symptoms')}
                >
                  <i className="fas fa-notes-medical bottom-nav-icon"></i>
                  <span className="bottom-nav-label">Sintomi</span>
                </div>
                <div 
                  className={`bottom-nav-item ${activeTab === 'wellness' ? 'active' : ''}`}
                  onClick={() => setActiveTab('wellness')}
                >
                  <i className="fas fa-spa bottom-nav-icon"></i>
                  <span className="bottom-nav-label">Benessere</span>
                </div>
                <div 
                  className={`bottom-nav-item ${activeTab === 'medications' ? 'active' : ''}`}
                  onClick={() => setActiveTab('medications')}
                >
                  <i className="fas fa-pills bottom-nav-icon"></i>
                  <span className="bottom-nav-label">Medicinali</span>
                </div>
                <div 
                  className={`bottom-nav-item ${activeTab === 'assistant' ? 'active' : ''}`}
                  onClick={() => setActiveTab('assistant')}
                >
                  <i className="fas fa-robot bottom-nav-icon"></i>
                  <span className="bottom-nav-label">Assistente</span>
                </div>
              </nav>
            </>
          )}
        </div>
      </main>

      {/* Welcome Modal */}
      {showWelcomeModal && (
        <div className="modal-overlay fade-in">
          <div className="modal-container slide-in-up">
            <div className="modal-header">
              <h3><i className="fas fa-hand-sparkles"></i> Benvenuto in Salus!</h3>
              <button className="btn-close" onClick={() => setShowWelcomeModal(false)}>
                <i className="fas fa-times"></i>
              </button>
            </div>
            <div className="modal-body">
              <p>Siamo felici di averti qui! Salus è il tuo assistente personale per monitorare e migliorare la tua salute.</p>
              <div className="welcome-features">
                <div className="feature">
                  <div className="feature-icon">
                    <i className="fas fa-notes-medical"></i>
                  </div>
                  <div className="feature-text">
                    <h4>Traccia i tuoi Sintomi</h4>
                    <p>Registra i tuoi sintomi e monitora la loro evoluzione nel tempo.</p>
                  </div>
                </div>
                <div className="feature">
                  <div className="feature-icon">
                    <i className="fas fa-spa"></i>
                  </div>
                  <div className="feature-text">
                    <h4>Diario del Benessere</h4>
                    <p>Annota il tuo umore, sonno e attività quotidiane.</p>
                  </div>
                </div>
                <div className="feature">
                  <div className="feature-icon">
                    <i className="fas fa-pills"></i>
                  </div>
                  <div className="feature-text">
                    <h4>Gestione Medicinali</h4>
                    <p>Tieni traccia dei tuoi medicinali e ricevi promemoria.</p>
                  </div>
                </div>
                <div className="feature">
                  <div className="feature-icon">
                    <i className="fas fa-robot"></i>
                  </div>
                  <div className="feature-text">
                    <h4>Assistente IA</h4>
                    <p>Chiedi consigli e ottieni risposte personalizzate sul tuo stato di salute.</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button className="btn btn-primary" onClick={() => setShowWelcomeModal(false)}>
                Iniziamo! <i className="fas fa-arrow-right"></i>
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Offline Indicator */}
      {!isOnline && (
        <div className="offline-indicator">
          <i className="fas fa-wifi-slash"></i> Sei offline. Alcune funzionalità potrebbero non essere disponibili.
        </div>
      )}

      <footer className="app-footer">
        <div className="container">
          <p className="text-center">
            <i className="fas fa-heartbeat"></i> Salus &copy; 2025 - La tua salute al primo posto
          </p>
        </div>
      </footer>

      {/* Toast container */}
      <div id="toast-container"></div>

      {/* CSS per componenti aggiuntivi */}
      <style jsx>{`
        /* Stili per il menu mobile */
        .mobile-menu {
          position: absolute;
          top: var(--header-height);
          left: 0;
          right: 0;
          background-color: var(--white);
          box-shadow: var(--shadow-md);
          z-index: 100;
        }

        .mobile-menu ul {
          list-style: none;
          padding: 0;
        }

        .mobile-menu li {
          padding: var(--space-4);
          border-bottom: 1px solid var(--gray-200);
          cursor: pointer;
          display: flex;
          align-items: center;
        }

        .mobile-menu li:hover {
          background-color: var(--gray-100);
        }

        .mobile-menu li i {
          margin-right: var(--space-3);
          color: var(--primary-600);
          width: 20px;
          text-align: center;
        }

        /* Toast notifications */
        #toast-container {
          position: fixed;
          bottom: 20px;
          right: 20px;
          z-index: 9999;
        }

        .toast {
          padding: var(--space-3) var(--space-4);
          border-radius: var(--radius-md);
          margin-bottom: var(--space-2);
          box-shadow: var(--shadow-md);
          min-width: 250px;
          position: relative;
          overflow: hidden;
        }

        .toast-success {
          background-color: var(--success);
          color: white;
        }

        .toast-error {
          background-color: var(--danger);
          color: white;
        }

        .toast-info {
          background-color: var(--info);
          color: white;
        }

        .toast-warning {
          background-color: var(--warning);
          color: var(--gray-900);
        }

        .fade-out {
          opacity: 0;
          transition: opacity 0.3s ease;
        }

        /* Modal */
        .modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background-color: rgba(0, 0, 0, 0.5);
          display: flex;
          justify-content: center;
          align-items: center;
          z-index: 1000;
        }

        .modal-container {
          background-color: var(--white);
          border-radius: var(--radius-lg);
          box-shadow: var(--shadow-xl);
          width: 90%;
          max-width: 500px;
          max-height: 80vh;
          overflow-y: auto;
        }

        .modal-header {
          padding: var(--space-4);
          border-bottom: 1px solid var(--gray-200);
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .modal-body {
          padding: var(--space-4);
        }

        .modal-footer {
          padding: var(--space-4);
          border-top: 1px solid var(--gray-200);
          display: flex;
          justify-content: flex-end;
        }

        .btn-close {
          background: none;
          border: none;
          font-size: var(--font-size-xl);
          cursor: pointer;
          color: var(--gray-600);
        }

        .btn-close:hover {
          color: var(--gray-900);
        }

        /* Welcome modal */
        .welcome-features {
          margin-top: var(--space-4);
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          gap: var(--space-4);
        }

        .feature {
          display: flex;
          align-items: flex-start;
          margin-bottom: var(--space-3);
        }

        .feature-icon {
          width: 40px;
          height: 40px;
          border-radius: var(--radius-full);
          background-color: var(--primary-100);
          color: var(--primary-600);
          display: flex;
          align-items: center;
          justify-content: center;
          margin-right: var(--space-3);
          font-size: var(--font-size-lg);
        }

        .feature-text h4 {
          margin-bottom: var(--space-1);
          color: var(--gray-900);
        }

        .feature-text p {
          color: var(--gray-600);
          font-size: var(--font-size-sm);
        }

        /* Offline indicator */
        .offline-indicator {
          position: fixed;
          bottom: 0;
          left: 0;
          right: 0;
          background-color: var(--danger);
          color: white;
          text-align: center;
          padding: var(--space-2);
          z-index: 1001;
        }

        /* Footer */
        .app-footer {
          background-color: var(--gray-900);
          color: var(--white);
          padding: var(--space-4) 0;
          margin-top: var(--space-6);
        }

        /* Utilitari */
        .w-100 { width: 100%; }
        .mt-4 { margin-top: var(--space-4); }
        .ml-2 { margin-left: var(--space-2); }
        .text-muted { color: var(--gray-600); }
        .text-primary { color: var(--primary-600); }
        .text-success { color: var(--success); }
        .text-danger { color: var(--danger); }

        /* Animazioni aggiuntive per elementi interattivi */
        .btn-primary:active,
        .btn-secondary:active,
        .btn-danger:active {
          transform: scale(0.98);
        }

        @media (max-width: 768px) {
          .welcome-features {
            grid-template-columns: 1fr;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
